import React,{useState} from 'react'
import Fields from './Fields'


function Table() {
    const emptyRow = {id:Math.floor(Math.random() * 999 )+1,name:"",data_type : "" ,null_value:false}
    const [table,setTable] = useState([])
    const [rerendering, setrerendering] = useState('')

    const updateRow  =(row) => {
        const tables = [ ...table]
        var index = tables.findIndex( r => r.id === row.id )
        tables[index] = row
        console.log('index',index,'row',row,'tables',tables);
        setTable(tables)
    }

    React.useEffect(()=>{

        var sgr = `create table ${props.t_name} (`;
        for (let index = 0; index < table.length; index++) {
          sgr +=
            table[index].col_name + "  " + table[index].col_dataType + ", \n";
        }
        sgr += ");";
        console.log("effect work", sgr);
        sql = sgr;


        setrerendering()
    },[table])

    return (
        <div>
   
            <table className="table  table-striped " >
                <thead>
                  <tr>
                  <th scope="row1" >id</th>
                    <th scope="row1" >name</th>
                    <th scope="row1" >data-type</th>
                    <th scope="row1" >null</th>
                  </tr>
                </thead>
                <tbody>
                   
                   {table.map((row,index) => <tr  key={row.id} ><Fields key={row.id} updateRow={updateRow} row={row} >{row.name}</Fields></tr> )}
                   
                   <tr><td><button className="btn btn-primary" onClick={()=>{setTable([ ...table,emptyRow])}} >new row</button></td></tr>

                </tbody>
            </table>     
         
        </div>
    )
}

export default Table
