import React,{useState} from 'react'

function Fields(props) {
    const {updateRow,row} = props
    const [name, setName] = useState(row.name)
    const [data_type, setdata_type] = useState(row.data_type)
    const [null_value, setnull_value] = useState(row.null_value)
    return (
        <React.Fragment>
            <td>{row.id}</td>
            <td><input onChange={(e)=>{setName(e.target.value)}} value={name} placeholder="row name" /></td>
            <td> <select  onChange={(e)=>{setdata_type(e.target.value)}} value={data_type} >
                <option>data</option>    
            </select> </td>
            <td><input onChange={()=>{setnull_value(!null_value)}}  value={null_value} type="checkbox" /></td>
            <td><button onSubmit={
                () => {
                    var newR = {
                        id : row.id,name : name , data_type : data_type, null_value : null_value
                    }
                    updateRow(newR)
                }
            } className="btn btn-primary m-2" >add</button></td>


        </React.Fragment>
    )
}

export default Fields
