import React,{useState} from 'react'
import Fields from './Fields'
function Table() {
    const emptyRow = {id:Math.floor(Math.random() * 999 )+1,name:"",data_type : "" ,null_value:false}
    const [table,setTable] = useState([])
    const [rerendering, setrerendering] = useState('')
    const updateRow = (row) => {
        const tables = [ ...table]
        var index = tables.indexOf(row)
        tables[index] = row
        setTable(tables)
    }

    React.useEffect(()=>{
        setrerendering({})
    },[table])

    return (
        <div>
            <table className="table table-dark table-striped" >
                <thead>
                  <tr>
                  <th scope="row1" >id</th>
                    <th scope="row1" >name</th>
                    <th scope="row1" >data-type</th>
                    <th scope="row1" >null</th>
                  </tr>
                </thead>
                <tbody>
                   
                   {table.map((row,index) => <tr key={row.id} updateRow={updateRow} row={row} >{row.name}</tr> )}
                  
                   <tr><td><button onClick={()=>{setTable([ ...table,emptyRow])}} >new row</button></td></tr>

                </tbody>
            </table>        
        </div>
    )
}

export default Table
